import cn from 'classnames';

const ProgressBar = (props) => {
    const { percentage } = props;

    const bgcolor = "#E5E5E5";
    const containerStyles = {
        height: 20,
        width: '100%',
        backgroundColor: "#e0e0de",
        borderRadius: 50,
        margin: 50
    }

    const fillerStyles = {
        height: '100%',
        width: `${percentage}%`,
        backgroundColor: bgcolor,
        borderRadius: 'inherit',
        textAlign: 'right'
    }

    const labelStyles = {
        padding: 5,
        color: 'white',
        fontWeight: 'bold'
    }

    return (
        <div className={cn(containerStyles)}>
            <div className={cn(fillerStyles)}>
                <span className={cn(labelStyles)}>{`${percentage}%`}</span>
            </div>
        </div>
    );
};

export default ProgressBar;
