import React, { FC, useCallback, useEffect } from 'react';
import { connect, DefaultRootState } from 'react-redux';
import { AppState, ProjectProperties } from '../../redux/appState';
import './styles.css';
import Menu from '../menu';
import Project from '../project';
import { ProjectLoadSuccess } from '../../redux/app/actions';
import { getProjects } from '../../redux/fetch';
import { AppDispatch } from '../../redux/store';
import { RootState } from '../../redux/app/reducer';

export interface AppOwnProps {

}

export interface AppStateProps {
  expand: boolean;
  projects: ProjectProperties[];
}

export interface AppDispatchProps {
  fetchProjects: () => void;
}

export type AppProps = AppOwnProps & AppStateProps & AppDispatchProps;

const App: FC<AppProps> = ({ fetchProjects }) => {
  useEffect(() => {
    console.log('invoke getProjectsAsync');
    fetchProjects();
  }, []);


  return (
    <div className="App">
      <Menu />
      <Project />
    </div>
  );
};

const mapStateToProps = (state: RootState): AppStateProps => ({
  expand: state.app.expand,
  projects: state.app.projects,
});

const mapDispatchToProps = (
  dispatch: AppDispatch
): AppDispatchProps => ({
  fetchProjects: () => dispatch(getProjects())
});

export default connect<AppStateProps, AppDispatchProps>(
  mapStateToProps,
  mapDispatchToProps
)(App);
