import { AppActions } from './actions';
import { ADD_PROJECT, ADD_LANGUAGES, EXPAND_PROJECTS, COLLAPSE_PROJECTS } from "../types";
import { AppState } from '../appState';
import { appInitialState } from '../../redux/initialState';

export const appReducer = (
    state: AppState = appInitialState,
    action: AppActions
): AppState => {
    switch (action.type) {
        case ADD_PROJECT:
            const id = state.projects.length + 1;
            const project = {
                ...action.project,
                id,
                name: `${action.project.name} ${id}`,
            };
            const projects = state.projects.slice();
            projects.push(project);
            return {
                ...state,
                projects
            };
        case ADD_LANGUAGES:
            const projects_2 = state.projects.slice();
            const selectedProject = projects_2.find(project => project.id === action.projectId);
            const languages = selectedProject.translationSections.slice();
            languages.push(action.languages);
            selectedProject.translationSections = languages;
            return {
                ...state,
                projects: projects_2
            };
        case EXPAND_PROJECTS:
            return {
                ...state,
                expand: true,
            };
        case COLLAPSE_PROJECTS:
            return {
                ...state,
                expand: false,
            };
        default:
            return {
                ...state,
            };
    }
};
