import cn from 'classnames';
import { FC } from 'react';

export interface ProgressbarProps {
    percentage,
    size: 'medium' | 'small';
}

const ProgressBar: FC<ProgressbarProps> = ({ percentage, size }) => {
    let width = 160;
    if (size === 'small') {
        width = 160;
    } else if (size === 'medium') {
        width = 205;
    }

    const bgcolor = "#E5E5E5";
    const containerStyles = {
        height: 20,
        width: '160px',
        'background-color': "#e0e0de",
        'border-radius': 3,
        'border-style': 'solid',
    }

    const fillerStyles = {
        width: `${percentage}%`,
    }

    const labelStyles = {
        padding: 5,
        color: 'white',
        'font-weight': 'bold'
    }

    return (
        <div style={containerStyles}>
            <div style={fillerStyles}></div>
        </div>
    );
};

export default ProgressBar;
